function getCloneArr(arr) {
    let cloneArr = [];

    for (let i = 0; i < arr.length; i++) {
        cloneArr.push(arr[i]);
    }

    return cloneArr;
}


function btnOnClick() {
    units.style.display = 'none';

    chooseLan.style.display = 'flex';

    container.style.height = '100%';

    flag = true;

    saveArr = getCloneArr(wordsArray);
}

