let choose_lan_btn_ua = document.getElementById('choose_lan_btn_ua');
let choose_lan_btn_en = document.getElementById('choose_lan_btn_en');

let firstWord;
let secondWord;

choose_lan_btn_ua.onclick = function (event) {
    firstWord = 0;
    secondWord = 1;

    if (window.screen.width < 575) {
        upper_block_text.style.fontSize = '30px';
        lower_block_text.style.fontSize = '28px';
    } else {
        upper_block_text.style.fontSize = '39px';
        lower_block_text.style.fontSize = '35px';
    }

    lanBtnOnclick();
}

choose_lan_btn_en.onclick = function (event) {
    firstWord = 1;
    secondWord = 0;

    if (window.screen.width < 575) {
        upper_block_text.style.fontSize = '28px';
        lower_block_text.style.fontSize = '30px';
    } else {
        upper_block_text.style.fontSize = '35px';
        lower_block_text.style.fontSize = '39px';
    }

    lanBtnOnclick();
}


function lanBtnOnclick() {
    upper_block.style.display = 'flex';
    lower_block.style.display = 'flex';

    chooseLan.style.display = 'none';
}


document.addEventListener('keyup', event => {

    if (event.code === 'Enter') {

        lower_block_text.style.opacity = '0';

        if (wordsArray.length != 0) {

            function randomInteger(min, max) {
                let rand = min + Math.random() * (max + 1 - min);
                return Math.floor(rand);
            }

            let randomNum = randomInteger(0, wordsArray.length - 1);

            let deleteWord = wordsArray.splice(randomNum, 1)[0];

            upper_block_text.textContent = deleteWord[firstWord];
            lower_block_text.textContent = deleteWord[secondWord];

        } else {

            upper_block_text.textContent = 'The end';
            lower_block_text.textContent = '';

            wordsArray = getCloneArr(saveArr);
        }
    }

    if (event.code === "ShiftRight") {
        lower_block_text.style.opacity = 1;
    }
});


upper_block.addEventListener('touchend', event => {

    if (flag === true) {

        lower_block_text.style.opacity = '0';

        if (wordsArray.length != 0) {

            function randomInteger(min, max) {
                let rand = min + Math.random() * (max + 1 - min);
                return Math.floor(rand);
            }

            let randomNum = randomInteger(0, wordsArray.length - 1);

            deleteWord = wordsArray.splice(randomNum, 1)[0];

            upper_block_text.textContent = deleteWord[firstWord];
            lower_block_text.textContent = deleteWord[secondWord];

        } else {

            upper_block_text.textContent = 'The end';
            lower_block_text.textContent = '';

            wordsArray = getCloneArr(saveArr);
        }

        lower_block.addEventListener('touchend', event => {
            lower_block_text.style.opacity = 1;
        });
    }
});